#include<iomanip> //定义控制器setprecision，指明输出的精度#include<ios> //定义了一个streamsize，表示输入输出的长度#include<iostream>#include<string>#include<algorithm>#include<vector>using namespace std;int main(int argc, char *argv[]){	cout<<"please enter your first name: ";	string name;	cin>>name;	cout<<"hello, "<<name<<"!"<<endl;	cout<<"please enter your midterm and final exam grades: ";	double midterm, final;	cin>>midterm>>final;	cout<<"enter all your homework grades, "		"followed by end-of-file: ";	vector<double> homework;	double x;	while(cin >> x){		homework.push_back(x);	}	typedef vector<double>::size_type vec_sz;	vec_sz size = homework.size();	if(size == 0){		cout<<endl<<"you must enter your grades. "			<<"please try again. "<<endl;		return 1;	}	sort(homework.begin(), homework.end());	vec_sz mid = size / 2;	double median;	median = (size % 2 == 0) ? (homework[mid] + homework[mid-1]) / 2 : 		homework[mid];	streamsize prec = cout.precision();	cout << "your first grade is " << setprecision(3)		 << 0.2 * midterm + 0.4 * final + 0.4 * median		 << setprecision(prec) << endl;	return 0;	}



